mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY o.ORDER_DATE DESC) AS rnum, o.ITEMS_ID AS orderNum, TO_CHAR(o.ORDER_DATE, 'YYYY-MM-DD') AS orderDate, NVL(o.TOTAL_AMOUNT, 0) AS totalAmount, o.STATUS AS orderState, NVL(p.PROD_NAME, '상품정보없음') AS productName, NVL(p.PROD_ID, 0) AS productNum, NVL(p.THUMB_NAIL, 'no-image.png') AS thumbNail,
NVL(oi.COUNT, 0) AS qty, NVL(oi.PRICE, 0) AS price, NVL(ps.PD_SIZE, '-') AS pdSize,
NVL(m2.userName, '탈퇴회원') AS userName, NVL(m1.userId,'unknown') AS userId, m2.tel FROM orders o LEFT JOIN order_items oi ON o.ITEMS_ID = oi.ORDER_ID LEFT JOIN pd_size ps ON oi.OPT_ID = ps.OPT_ID LEFT JOIN products p ON ps.PROD_ID = p.PROD_ID LEFT JOIN member1 m1 ON o.MEMBERIDX = m1.MEMBERIDX LEFT JOIN member2 m2 ON o.MEMBERIDX = m2.MEMBERIDX
AND o.MEMBERIDX = #{memberIdx}
AND o.STATUS = #{orderState}
AND o.STATUS IN ('취소신청', '취소처리중', '취소완료', '반품신청', '반품진행중', '반품완료', '환불완료', '교환신청', '교환완료')
AND o.STATUS IN ('취소신청', '취소처리중', '취소완료', '반품신청', '반품진행중', '반품완료', '환불완료', '교환신청', '교환완료')
AND o.ORDER_DATE >= TO_DATE(#{historyStartDate}, 'YYYY-MM-DD')
AND o.ORDER_DATE < TO_DATE(#{historyEndDate}, 'YYYY-MM-DD') + 1
AND o.ORDER_DATE >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND o.ORDER_DATE < TO_DATE(#{endDate}, 'YYYY-MM-DD') + 1
AND o.STATUS = #{status}
AND (o.ITEMS_ID LIKE '%' || #{searchValue} || '%' OR m2.userName LIKE '%' || #{searchValue} || '%')
AND o.STATUS IN ('취소신청', '취소처리중', '취소완료', '반품신청', '반품진행중', '반품완료', '환불완료', '교환신청', '교환완료')
) WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:
UPDATE orders
SET
STATUS = #{status}
WHERE ITEMS_ID = #{orderNum}

mapper.insert:
INSERT INTO claims (
claim_id, ITEMS_ID, type, reason, status, req_date
) VALUES (
claims_seq.NEXTVAL, #{orderId}, #{claimType}, #{reason},
#{claimStatus}, SYSDATE
)

mapper.update:
UPDATE orders
SET
STATUS = #{status}
WHERE ITEMS_ID = #{orderNum}

UPDATE pd_size
SET STOCK = STOCK + #{qty}
WHERE PROD_ID = #{productNum} AND PD_SIZE =
#{pdSize}
