mapper.update:
UPDATE board SET 
	replyContent = #{replyContent},
	replyDate = SYSDATE,
	replyId = #{replyId}
WHERE num = #{num}

mapper.select:
SELECT NVL(COUNT(*), 0) FROM notice


		AND 


		AND isShow = #{isShow}

mapper.update:
UPDATE notice SET 
	subject=#{subject}, content=#{content}, 
	isNotice=#{isNotice}, isShow=#{isShow},
	saveFilename=#{saveFilename, jdbcType=VARCHAR},
	originalFilename=#{originalFilename, jdbcType=VARCHAR}
WHERE num = #{num}

mapper.select:
SELECT num, category, subject, content FROM faq


		AND category = #{category}


ORDER BY num DESC
