mapper.update:
UPDATE member1
SET userPwd = #{userPwd},
nickName = #{nickName},
gender = #{gender},
modify_date = SYSDATE

	, userLevel = #{userLevel}


	, enabled = #{enabled}

WHERE memberIdx = #{memberIdx}

UPDATE member2 SET userName = #{userName}, email =
#{email},
tel = #{tel, jdbcType=VARCHAR}
WHERE memberIdx = #{memberIdx}

mapper.select:
SELECT memberIdx, userId, nickName
FROM member1
WHERE nickName = #{nickName}

mapper.update:
UPDATE member1 SET
userLevel = #{userLevel}
WHERE userId = #{userId}

UPDATE member1 SET
enabled = #{enabled}
WHERE memberIdx = #{memberIdx}

UPDATE member2 SET
profile_photo = ''
WHERE userId = #{userId}

UPDATE member1 SET
enabled = 0, userLevel = 0
WHERE memberIdx = #{memberIdx}

mapper.insert:
INSERT INTO address (
addr_id, memberIdx, addr_name, receiver_name, receiver_tel,
zip_code, addr1, addr2, isbasic
) VALUES (
addr_seq.NEXTVAL, #{memberIdx}, #{addrName, jdbcType=VARCHAR}, #{receiverName},
#{receiverTel},
#{zipCode}, #{addr1}, #{addr2}, #{isBasic}
)
